mapper.select:
select d.id, d.name, d.category_id, d.price, d.code, d.image, d.description, d.status, d.sort, d.create_time, d.update_time, d.create_user, d.update_user, d.is_deleted, c.name as categoryName from dish d left join category c on c.id = d.category_id where is_deleted = 0
and d.name like '%' #{name} '%'
limit #{start}, #{size}

mapper.update:
update dish
set status = #{code}
where id in

  #{id}
