mapper.select:
select s.id, s. category_id, s. name, s. price, s. status, s. code, s. description, s. image, s. create_time, s. update_time, s. create_user, s. update_user, s. is_deleted, c.name as categoryName from setmeal s left join category c on c.id = s.category_id where is_deleted = 0
and c.name like '%' #{name} '%'
limit #{start}, #{size}

mapper.update:
update setmeal set is_deleted = 1
where id in

  #{id}

update setmeal
set status = #{code}
where id in

  #{id}
